matched as (
  select vfd.*, s.id as subscription_id, i.display_name as db_investor, i.legal_name as db_legal,
         s.commitment as db_commitment, s.funded_amount as db_funded_amount, s.num_shares as db_shares,
         s.contract_date as db_contract_date, s.subscription_fee_amount as db_subscription_fee_amount,
         s.spread_fee_amount as db_spread_fee_amount, s.bd_fee_amount as db_bd_fee_amount, s.finra_fee_amount as db_finra_fee_amount,
         s.price_per_share as db_price_per_share, s.cost_per_share as db_cost_per_share,
         s.performance_fee_tier1_percent as db_pf1, s.performance_fee_tier1_threshold as db_pf1_th,
         s.performance_fee_tier2_percent as db_pf2, s.performance_fee_tier2_threshold as db_pf2_th,
         s.spread_per_share as db_spread_per_share, s.management_fee_percent as db_management_fee_percent,
         s.discount_rate as db_discount_rate, s.interest_rate as db_interest_rate, s.valuation_cap as db_valuation_cap,
         s.opportunity_name as db_opportunity_name, s.sourcing_contract_ref as db_sourcing_contract_ref
  from vfd
  left join vehicles v on v.entity_code = vfd.entity_code
  left join subscriptions s on s.vehicle_id = v.id
     and s.commitment = vfd.commitment
     and s.num_shares = vfd.shares
     and ( (s.contract_date is null and vfd.contract_date is null) or s.contract_date = vfd.contract_date )
  left join investors i on i.id = s.investor_id
),
with_diffs as (
  select *,
    array_remove(ARRAY[
      CASE WHEN commitment IS DISTINCT FROM db_commitment THEN 'commitment' END,
      CASE WHEN funded_amount IS DISTINCT FROM db_funded_amount THEN 'funded_amount' END,
      CASE WHEN shares IS DISTINCT FROM db_shares THEN 'shares' END,
      CASE WHEN contract_date IS DISTINCT FROM db_contract_date THEN 'contract_date' END,
      CASE WHEN subscription_fee_amount IS DISTINCT FROM db_subscription_fee_amount THEN 'subscription_fee_amount' END,
      CASE WHEN spread_fee_amount IS DISTINCT FROM db_spread_fee_amount THEN 'spread_fee_amount' END,
      CASE WHEN bd_fee_amount IS DISTINCT FROM db_bd_fee_amount THEN 'bd_fee_amount' END,
      CASE WHEN finra_fee_amount IS DISTINCT FROM db_finra_fee_amount THEN 'finra_fee_amount' END,
      CASE WHEN price_per_share IS DISTINCT FROM db_price_per_share THEN 'price_per_share' END,
      CASE WHEN cost_per_share IS DISTINCT FROM db_cost_per_share THEN 'cost_per_share' END,
      CASE WHEN performance_fee_tier1_percent IS DISTINCT FROM db_pf1 THEN 'performance_fee_tier1_percent' END,
      CASE WHEN performance_fee_tier1_threshold IS DISTINCT FROM db_pf1_th THEN 'performance_fee_tier1_threshold' END,
      CASE WHEN performance_fee_tier2_percent IS DISTINCT FROM db_pf2 THEN 'performance_fee_tier2_percent' END,
      CASE WHEN performance_fee_tier2_threshold IS DISTINCT FROM db_pf2_th THEN 'performance_fee_tier2_threshold' END,
      CASE WHEN spread_per_share IS DISTINCT FROM db_spread_per_share THEN 'spread_per_share' END,
      CASE WHEN management_fee_percent IS DISTINCT FROM db_management_fee_percent THEN 'management_fee_percent' END,
      CASE WHEN discount_rate IS DISTINCT FROM db_discount_rate THEN 'discount_rate' END,
      CASE WHEN interest_rate IS DISTINCT FROM db_interest_rate THEN 'interest_rate' END,
      CASE WHEN valuation_cap IS DISTINCT FROM db_valuation_cap THEN 'valuation_cap' END,
      CASE WHEN opportunity_name IS DISTINCT FROM db_opportunity_name THEN 'opportunity_name' END,
      CASE WHEN sourcing_contract_ref IS DISTINCT FROM db_sourcing_contract_ref THEN 'sourcing_contract_ref' END
    ], NULL) as diff_fields
  from matched
)
select row_num, entity_code, investor_name, subscription_id, db_investor, db_legal,
       diff_fields,
       commitment, db_commitment,
       funded_amount, db_funded_amount,
       shares, db_shares,
       contract_date, db_contract_date,
       subscription_fee_amount, db_subscription_fee_amount,
