       performance_fee_tier2_percent, db_pf2,
       performance_fee_tier2_threshold, db_pf2_th,
       spread_per_share, db_spread_per_share,
       management_fee_percent, db_management_fee_percent,
       discount_rate, db_discount_rate,
       interest_rate, db_interest_rate,
       valuation_cap, db_valuation_cap,
       opportunity_name, db_opportunity_name,
