matched as (
  select vfd.*, s.id as subscription_id, i.display_name as db_investor, i.legal_name as db_legal,
         s.commitment as db_commitment, s.funded_amount as db_funded_amount, s.num_shares as db_shares,
         s.contract_date as db_contract_date, s.subscription_fee_amount as db_sub_fee, s.spread_fee_amount as db_spread_fee,
         s.bd_fee_amount as db_bd_fee, s.finra_fee_amount as db_finra_fee, s.price_per_share as db_price_per_share,
         s.cost_per_share as db_cost_per_share, s.performance_fee_tier1_percent as db_pf1,
         s.performance_fee_tier1_threshold as db_pf1_th, s.performance_fee_tier2_percent as db_pf2, s.performance_fee_tier2_threshold as db_pf2_th,
         s.spread_per_share as db_spread_per_share, s.management_fee_percent as db_mgmt_fee, s.discount_rate as db_discount_rate,
         s.interest_rate as db_interest_rate, s.valuation_cap as db_valuation_cap, s.opportunity_name as db_opportunity_name,
         s.sourcing_contract_ref as db_sourcing_contract_ref
  from vfd
  left join vehicles v on v.entity_code = vfd.entity_code
  left join subscriptions s on s.vehicle_id = v.id
     and s.commitment = vfd.commitment
     and s.num_shares = vfd.shares
     and ( (s.contract_date is null and vfd.contract_date is null) or s.contract_date = vfd.contract_date )
  left join investors i on i.id = s.investor_id
)
select * from matched order by entity_code, row_num;
