        const { data: referral } = await serviceSupabase.from('deal_memberships').select('deal_id').eq('deal_id', dealId).eq('referred_by_entity_type', 'partner').eq('referred_by_entity_id', partnerPersona.entity_id).limit(1).maybeSingle();
        if (referral) {
            return {
                allowed: true
            };
        }
